// Banner.js
import React from 'react';
import eventImage from '../assets/event_01.png'; // Make sure to adjust the path according to your project structure

const Banner = () => {
  return (
    <div className="banner">
      <div className="text-content">
        <h1>무슨 강의를 들을지 고민이라면?</h1>
        <h2><span style={{color: "#007bff"}}>클릭</span> 한번으로 맞춤 강의 추천받기!</h2>
        <button className="click-here-button">Click Here!</button>
      </div>
      <img src={eventImage} alt="Event" className="banner-image" />
    </div>
  );
};

export default Banner;
