// 기존 Banner.js 파일을 수정하여 새로운 디자인으로 변경
import React from 'react';
import nextjsImage from '../assets/event_01.png'; // 이미지 경로는 프로젝트에 맞게 수정하세요
import awsRedisImage from '../assets/event_02.png';
import rustImage from '../assets/event_03.png';

const Banner = () => {
  return (
    <div className="banner">
      <h1 className="section-title">개발자라면 한번쯤</h1>
      <p className="section-description">
        <span>Next.js, Rust, Svelte, Quasar...</span><br/>
        취미로 한 번쯤 다뤄보고 싶었던 기술이 있다면<br/>
        국내에 흔하지 않은 주제의 강의를 찾는다면
      </p>
      <div className="course-cards">
        <div className="course-card">
          <img src={nextjsImage} alt="Next.js" />
          <h3>[손에 읽는 Next.js] Part 1 - 공식 문서 훑어보기</h3>
          <p>25,300원</p>
        </div>
        <div className="course-card">
          <img src={awsRedisImage} alt="AWS Redis" />
          <h3>하루만에 배우는 AWS REDIS</h3>
          <p>29,700원</p>
        </div>
        <div className="course-card">
          <img src={rustImage} alt="Rust" />
          <h3>우아한 고성능 프로그래밍 언어 Rust 입문 및 활용</h3>
          <p>55,000원</p>
        </div>
      </div>
    </div>
  );
};

export default Banner;
